mapper.select:
SELECT COUNT(1)
FROM moments_like
WHERE post_id = #{momentId}
  AND user_id = #{userId}

mapper.update:
UPDATE moments_post
SET like_count = like_count + 1
WHERE id = #{momentId}

UPDATE moments_post
SET like_count = like_count - 1
WHERE id = #{momentId}

mapper.delete:
DELETE FROM moments_like
WHERE post_id = #{postId}
  AND user_id = #{userId}
